mapper.insert:

	select nvl(max(alarm_no),0)+1 as alarm_no
	from alarm


INSERT INTO alarm (
    alarm_no,
    mem_no,
    alarm_cont,
    alarm_create_date,
    alarm_type
) VALUES (
    #{alarm_no}
    ,#{mem_no, jdbcType=VARCHAR}
    ,#{alarm_cont, jdbcType=VARCHAR}
    ,sysdate
    ,#{alarm_type, jdbcType=VARCHAR}
)
