mapper.select:
	SELECT MEM_NO, MEM_ID, MEM_NICK, MEM_PIC_FILE_NAME,
			MEM_NM, MEM_BIO, MEM_WORKING_CITY ,MEM_STATUS
	FROM MEMBER
 	WHERE MEM_NO NOT IN #{mem_no}

	        and (mem_id like #{keyword}
			    or mem_nick like #{keyword}	  
	            or mem_nm like #{keyword}
	            or mem_bio like #{keyword}	
	            or mem_working_city like #{keyword}
	        )
