mapper.update:
	UPDATE MEMBER
		SET


	    , MEM_NICK = #{mem_nick}


	    , MEM_TEL = #{MEM_TEL}


	    , MEM_PIC_FILE_NAME = #{MEM_PIC_FILE_NAME}


	    , MEM_NM = #{MEM_NM}


	    , MEM_BIO = #{MEM_BIO}


	    , MEM_WORKING_CITY = #{MEM_WORKING_CITY}


        WHERE MEM_NO = #{bo_no}

	 UPDATE MEMBER
	 	SET MEM_QUIT_DATE = SYSDATE
        WHERE MEM_NO = #{mem_no}
