mapper.select:
SELECT DISTINCT ISSUE.ISSUE_SID AS ISSUE_ID, ISSUE.ISSUE_NO, ISSUE.ISSUE_TITLE,ISSUE.ISSUE_STATUS,ISSUE.PROGRESS,ISSUE.ISSUE_PRIORITY, MILEST.MILEST_SID, MILEST.MILEST_NO, MILEST.MILEST_TITLE,MILEST.MILEST_CONT,MILEST.MILEST_CONT,MILEST.MILEST_DATE,MILEST.MILEST_STATUS, TO_CHAR(MILEST.MILEST_START_DATE,'YYYY-MM-DD') AS MILEST_START_DATE , TO_CHAR(MILEST.MILEST_END_DATE,'YYYY-MM-DD') AS MILEST_END_DATE, LABEL.LABEL_NO, LABEL.LABEL_NM, ISSUE_WRI_MEM.MEM_NO AS ISSUE_WRI_MEM_NO, ISSUE_WRI_MEM.MEM_PIC_FILE_NAME AS ISSUE_WRI_MEM_PIC,ISSUE.MEM_NO AS ISSUE_WRI_MEM_NO, ISSUE_WRI_NICK.PROJ_USER_NICK AS ISSUE_WRITER, MILEST.MEM_NO, MILEST_WRI_NICK.PROJ_USER_NICK AS MILEST_WRITER, IS_ASSIGNEE.MEM_NO AS ISSUE_ASSIN_MEM_NO, ISSUE_ASSIGN_MEM.MEM_PIC_FILE_NAME AS ISSUE_ASSIGN_MEM_PIC, IS_ASSIGN_MEM_NICK.PROJ_USER_NICK AS ISSUE_ASIGN_MEM, (SELECT SUM(A.ISSUE_STATUS)/COUNT(A.ISSUE_STATUS)*100 FROM(SELECT MILESTONE.*, ISSUE.ISSUE_TITLE, ISSUE.ISSUE_STATUS FROM MILESTONE LEFT OUTER JOIN ISSUE ON(MILESTONE.MILEST_SID = ISSUE.MILEST_SID) WHERE MILEST_NO = #{search.milest_no,jdbcType=NUMERIC} AND MILESTONE.PROJ_NO = #{search.proj_no,jdbcType=NUMERIC})A) AS MILEST_PERCENT, (SELECT COUNT (A.ISSUE_STATUS) FROM(SELECT ISSUE.ISSUE_STATUS FROM MILESTONE LEFT OUTER JOIN ISSUE ON(MILESTONE.MILEST_SID = ISSUE.MILEST_SID) WHERE MILESTONE.MILEST_NO = #{search.milest_no,jdbcType=NUMERIC} AND MILESTONE.PROJ_NO = #{search.proj_no,jdbcType=NUMERIC})A WHERE ISSUE_STATUS = 1) AS CLOSE_ISSUE_CNT, (SELECT COUNT (A.ISSUE_STATUS) FROM(SELECT ISSUE.ISSUE_STATUS FROM MILESTONE LEFT OUTER JOIN ISSUE ON(MILESTONE.MILEST_SID = ISSUE.MILEST_SID) WHERE MILESTONE.MILEST_NO = #{search.milest_no,jdbcType=NUMERIC} AND MILESTONE.PROJ_NO = #{search.proj_no,jdbcType=NUMERIC})A WHERE ISSUE_STATUS = 0) AS OPEN_ISSUE_CNT, (SELECT COUNT(*) -1 FROM ISSUE_HISTORY WHERE ISSUE_HISTORY.ISSUE_SID = ISSUE.ISSUE_SID AND ISSUE_HISTORY.ISSUE_HIS_TYPE = 'RE' ) AS REPLYCOUNT
FROM MILESTONE MILEST LEFT OUTER JOIN ( SELECT * FROM ISSUE
issue_status = #{search.issue_status ,jdbcType=NUMERIC}
) ISSUE ON (MILEST.MILEST_SID = ISSUE.MILEST_SID) LEFT OUTER JOIN LABEL LABEL ON (ISSUE.PROJ_NO = LABEL.PROJ_NO AND LABEL.LABEL_NO = ISSUE.LABEL_NO) LEFT OUTER JOIN MEMBER ISSUE_WRI_MEM ON (ISSUE.MEM_NO = ISSUE_WRI_MEM.MEM_NO) LEFT OUTER JOIN PROJ_MEM ISSUE_WRI_NICK ON (ISSUE.PROJ_NO = ISSUE_WRI_NICK.PROJ_NO AND ISSUE_WRI_MEM.MEM_NO = ISSUE_WRI_NICK.MEM_NO) INNER JOIN MEMBER MILEST_WRI_MEM ON (MILEST.MEM_NO = MILEST_WRI_MEM.MEM_NO) LEFT OUTER JOIN PROJ_MEM MILEST_WRI_NICK ON (MILEST.PROJ_NO = MILEST_WRI_NICK.PROJ_NO AND MILEST_WRI_MEM.MEM_NO = MILEST_WRI_NICK.MEM_NO) LEFT OUTER JOIN ISSUE_ASSIGNEE IS_ASSIGNEE ON(IS_ASSIGNEE.ISSUE_SID = ISSUE.ISSUE_SID) LEFT OUTER JOIN MEMBER ISSUE_ASSIGN_MEM ON (ISSUE_ASSIGN_MEM.MEM_NO = IS_ASSIGNEE.MEM_NO) LEFT OUTER JOIN PROJ_MEM IS_ASSIGN_MEM_NICK ON (IS_ASSIGN_MEM_NICK.MEM_NO = ISSUE_ASSIGN_MEM.MEM_NO AND IS_ASSIGN_MEM_NICK.PROJ_NO = ISSUE.PROJ_NO) WHERE MILEST.MILEST_NO = #{search.milest_no,jdbcType=NUMERIC} AND MILEST.PROJ_NO = #{search.proj_no,jdbcType=NUMERIC}
ORDER BY ISSUE_NO DESC

mapper.update:
	update milestone
       set  milest_title = #{milest_title,jdbcType=VARCHAR}
           ,milest_cont = #{milest_cont,jdbcType=VARCHAR}
           ,milest_date = sysdate
           ,milest_start_date = TO_DATE(#{milest_start_date,jdbcType=VARCHAR},'YYYY-MM-DD')
           ,milest_end_date = TO_DATE(#{milest_end_date,jdbcType=VARCHAR},'YYYY-MM-DD')
           ,milest_status = #{milest_status,jdbcType=NUMERIC}
      where milest_sid = #{milest_sid,jdbcType=NUMERIC}
