mapper.select:
select proj_no
from project 
    inner join member on (project.mem_no = member.mem_no)
where proj_title = #{project_title}
    and mem_id = #{manager_id}

select proj_user_nick
from proj_mem
where mem_no = #{mem_no}
	and proj_no = #{proj_no}

select mem_role_no, mem_role_nm
from mem_role
where proj_no = #{proj_no}

mapper.update:
UPDATE proj_mem
SET
    mem_role_no = ${mem_role_no}
WHERE
        mem_no = ${mem_no}
    AND proj_no = ${proj_no}
